CREATE OR REPLACE PROCEDURE sus_exclui_proc_contas_apac ( nr_apac_p bigint, nr_sequencia_p bigint, nr_atendimento_p bigint, cd_motivo_exc_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		bigint;

C01 CURSOR FOR 
	SELECT	c.nr_sequencia 
	from	sus_apac_unif a, 
		conta_paciente b, 
		procedimento_paciente c 
	where	a.nr_atendimento 	= nr_atendimento_p 
	and	a.nr_apac	= nr_apac_p 
	and	a.nr_sequencia	> nr_sequencia_p 
	and	a.nr_interno_conta	= b.nr_interno_conta 
	and	b.nr_interno_conta	= c.nr_interno_conta 
	and	coalesce(c.cd_motivo_exc_conta::text, '') = '';


BEGIN 
 
if (coalesce(cd_motivo_exc_p,'X') <> 'X') then 
	begin 
	 
	open C01;
	loop 
	fetch C01 into 
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
	 
		update 	procedimento_paciente 
		set	cd_motivo_exc_conta	= cd_motivo_exc_p, 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p 
		where	nr_sequencia		= nr_sequencia_w;
		 
		CALL sus_atualiza_valor_proc(nr_sequencia_w,nm_usuario_p);
		 
		end;
	end loop;
	close C01;	
	 
	commit;
	 
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
