CREATE OR REPLACE PROCEDURE sus_fechar_contas_apac_dt_ocor (nr_apac_p bigint, nr_sequencia_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_interno_conta_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	sus_apac_unif 
	where	nr_atendimento 	= nr_atendimento_p 
	and	nr_apac		= nr_apac_p 
	and	nr_sequencia	<> nr_sequencia_p;


BEGIN 
 
open C01;
loop 
fetch C01 into 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	update 	conta_paciente 
	set	ie_status_acerto	= 2, 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_interno_conta	= nr_interno_conta_w 
	and	nr_atendimento		= nr_atendimento_p;
	 
	CALL recalcular_conta_paciente(nr_interno_conta_w,nm_usuario_p);
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
