CREATE OR REPLACE PROCEDURE sus_fechar_conta_bpa ( nr_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
ie_fecha_atend_w		varchar(1);
ds_erro_w			varchar(2000);
cd_setor_atendimento_w		bigint;


BEGIN 
 
ie_fecha_atend_w := Obter_Param_Usuario(1125, 9, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_fecha_atend_w);
 
begin 
select	cd_setor_atendimento 
into STRICT	cd_setor_atendimento_w 
from	atendimento_paciente_v 
where	nr_atendimento	= nr_atendimento_p;
exception 
when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(193532,'NR_ATENDIMENTO='||nr_atendimento_p);
end;
 
if (ie_fecha_atend_w	in ('S','D')) then 
	ds_erro_w := Finalizar_Atendimento(nr_atendimento_p, 'S', nm_usuario_p, ds_erro_w);
end if;
 
if (ie_fecha_atend_w	= 'D') then 
	CALL Saida_Setor_Servico(nr_atendimento_p,cd_setor_atendimento_w,null, clock_timestamp(), nm_usuario_p);
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
