CREATE OR REPLACE PROCEDURE sus_fechar_conta_colmeia ( nr_interno_conta_p text, nr_atendimento_p text, nr_seq_protocolo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
qt_registro_w			bigint;
nr_protocolo_w			conta_paciente.nr_protocolo%type;
dt_mesano_referencia_w		conta_paciente.dt_mesano_referencia%type;
ie_insere_prot_w		varchar(1);
ds_erro_w			varchar(255) := '';


BEGIN 
 
if (coalesce(nr_interno_conta_p,'X') <> 'X') then 
	begin 
	 
	ie_insere_prot_w := obter_param_usuario(1124, 143, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_insere_prot_w);
	 
	CALL sus_consiste_conta(nr_interno_conta_p, nr_atendimento_p, nm_usuario_p);
 
	begin 
	select	count(1) 
	into STRICT	qt_registro_w 
	from	sus_inconsistencia b, 
		sus_consistencia_conta a 
	where	a.nr_seq_inconsistencia = b.nr_sequencia 
	and	a.nr_interno_conta = nr_interno_conta_p 
	and	coalesce(Sus_Obter_Se_Consiste_Estab(b.nr_sequencia, a.cd_estabelecimento, 'C'),b.ie_consiste) = 'S'  LIMIT 1;
	exception 
	when others then 
		qt_registro_w := 0;
	end;
	 
	ds_erro_w := fechar_conta_paciente(nr_interno_conta_p, nr_atendimento_p, 2, nm_usuario_p, ds_erro_w);	
	 
	if (coalesce(qt_registro_w,0) <> 0) then 
		begin	 
		insert into sus_conta_inco_colmeia( 
			nr_sequencia, 
			dt_atualizacao, 
			dt_atualizacao_nrec, 
			nm_usuario, 
			nm_usuario_nrec, 
			nr_atendimento, 
			nr_interno_conta, 
			nr_seq_protocolo) 
		values (	nextval('sus_conta_inco_colmeia_seq'), 
			clock_timestamp(), 
			clock_timestamp(), 
			nm_usuario_p, 
			nm_usuario_p, 
			nr_atendimento_p, 
			nr_interno_conta_p, 
			nr_seq_protocolo_p);
		end;
	end if;	
	 
	if	((coalesce(ds_erro_w,'X') = 'X') and 
		((ie_insere_prot_w = 'S') or 
		((ie_insere_prot_w = 'N') and (coalesce(qt_registro_w,0) = 0)))) then 
		begin 
		 
		begin 
		select	nr_protocolo, 
			dt_mesano_referencia 
		into STRICT	nr_protocolo_w, 
			dt_mesano_referencia_w 
		from	protocolo_convenio 
		where	nr_seq_protocolo = nr_seq_protocolo_p;
		exception 
		when others then 
			nr_protocolo_w := null;
			dt_mesano_referencia_w := null;
		end;
		 
		update	conta_paciente 
		set	nr_seq_protocolo	= nr_seq_protocolo_p, 
			nr_protocolo		= nr_protocolo_w, 
			dt_mesano_referencia	= dt_mesano_referencia_w, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_interno_conta = nr_interno_conta_p;
 
		end;
	end if;
 
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
