SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_gerar_cns_automatico ( cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_carteira_sus_w	bigint;
nr_cartao_nac_sus_w	varchar(20);

BEGIN

select	max(nr_cartao_nac_sus)
into STRICT	nr_cartao_nac_sus_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (coalesce(nr_cartao_nac_sus_w::text, '') = '') then

	select	min(nr_cns)
	into STRICT	nr_cartao_nac_sus_w
	from	CNS_LOTE_CARTEIRA_SUS
	where	coalesce(IE_CODIGO_UTILIZADO,'N') = 'N';

	if (nr_cartao_nac_sus_w IS NOT NULL AND nr_cartao_nac_sus_w::text <> '') then
		update	pessoa_fisica
		set		nr_cartao_nac_sus	= nr_cartao_nac_sus_w
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

		select	max(nr_sequencia)
		into STRICT	nr_seq_carteira_sus_w
		from	CNS_LOTE_CARTEIRA_SUS
		where	nr_cns		= nr_cartao_nac_sus_w;
		insert into cns_usuario_envio(	nr_sequencia,
										dt_atualizacao,
										nm_usuario,
										dt_atualizacao_nrec,
										nm_usuario_nrec,
										cd_pessoa_fisica,
										nr_seq_carteira_sus)
							values (	nextval('cns_usuario_envio_seq'),
										clock_timestamp(),
										nm_usuario_p,
										clock_timestamp(),
										nm_usuario_p,
										cd_pessoa_fisica_p,
										nr_seq_carteira_sus_w);

		update	CNS_LOTE_CARTEIRA_SUS
		set		ie_codigo_utilizado = 'S'
		where	nr_sequencia		= nr_seq_carteira_sus_w;

	end if;


end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
