CREATE OR REPLACE PROCEDURE sus_gerar_dados_comp_opme ( nr_interno_conta_p bigint, nr_nota_fiscal_p bigint, nr_lote_p text, cd_cgc_fabricante_p text, nr_serie_p text, nr_reg_anvisa_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
					
c01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	procedimento_paciente a
	where	a.nr_interno_conta = nr_interno_conta_p
	and	sus_validar_regra(4,a.cd_procedimento,a.ie_origem_proced,a.dt_procedimento) > 0
	and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
	and	not exists (	SELECT	1
				from	sus_aih_opm b
				where	b.nr_seq_procedimento = a.nr_sequencia);
					

BEGIN

open c01;
loop
fetch c01 into	
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	
	insert into sus_aih_opm(
		nr_sequencia,
		nr_seq_procedimento,
		nr_nota_fiscal,
		nr_lote,
		cd_cgc_fabricante,
		nr_serie,
		nr_reg_anvisa,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
	values (nextval('sus_aih_opm_seq'),
		nr_sequencia_w,
		nr_nota_fiscal_p,
		substr(nr_lote_p,1,20),
		substr(cd_cgc_fabricante_p,1,14),
		substr(nr_serie_p,1,20),
		substr(nr_reg_anvisa_p,1,20),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p);
	
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
