			);
		exception 
		when others then 
			nr_seq_interno_w := null;			
			ds_erro_w := substr(Wheb_mensagem_pck.get_Texto(301532, 'DS_ERRO_W='|| sqlerrm),1,2000);
		end;
 
		if (coalesce(ds_erro_w,'X') <> 'X') then 
			begin 
			 
			begin 
			insert into SUS_ERRO_IMP_COLMEIA( 
				ds_erro, 
				dt_atualizacao, 
				nm_usuario, 
				dt_atualizacao_nrec, 
				nm_usuario_nrec, 
				nr_atendimento, 
				nr_seq_interno, 
				nr_sequencia) 
			values (	ds_erro_w, 
				clock_timestamp(), 
				nm_usuario_p, 
				clock_timestamp(), 
				nm_usuario_p, 
				nr_atendimento_w, 
				nr_seq_interno_w, 
				nextval('sus_erro_imp_colmeia_seq'));			
			exception 
			when others then 
				insert into SUS_ERRO_IMP_COLMEIA( 
					ds_erro, 
					dt_atualizacao, 
					nm_usuario, 
					dt_atualizacao_nrec, 
					nm_usuario_nrec, 
					nr_atendimento, 
					nr_seq_interno, 
					nr_sequencia) 
				values (	'nr_atendimento_w:'|| nr_atendimento_w || 
					',nr_seq_interno_w:'|| nr_seq_interno_w, 
					clock_timestamp(), 
					nm_usuario_p, 
					clock_timestamp(), 
					nm_usuario_p, 
					null, 
					null, 
					nextval('sus_erro_imp_colmeia_seq'));
			end;
			 
			end;
		end if;
		 
		if (nr_seq_interno_w IS NOT NULL AND nr_seq_interno_w::text <> '') then 
			begin 
 
			CALL sus_consiste_laudo(nr_seq_interno_w, nm_usuario_p);
			CALL sus_liberar_laudo_colmeia(nr_seq_interno_w,nm_usuario_p,nr_atendimento_w,nr_apac_p);
 
			begin	--busca o n√∫mero da conta criada no momento. 
			select	max(nr_interno_conta) 
			into STRICT	nr_interno_conta_p 
			from	conta_paciente 
			where	nr_atendimento = nr_atendimento_w;
			exception 
			when others then 
				nr_interno_conta_p := 0;
			end;
 
			end;
		end if;
 
		end;
	end if;
 
	end;
else 
	begin 
	 
	ds_erro_w := substr('nr_apac_p:'|| nr_apac_p ||';nr_atendimento_p:'|| nr_atendimento_p || ':cd_proced_solic_w:'|| cd_proced_solic_w,1,2000);
	 
	insert into sus_erro_imp_colmeia( 
				ds_erro, 
				dt_atualizacao, 
				nm_usuario, 
				dt_atualizacao_nrec, 
				nm_usuario_nrec, 
				nr_atendimento, 
				nr_seq_interno, 
				nr_sequencia) 
			values (	ds_erro_w, 
				clock_timestamp(), 
				nm_usuario_p, 
				clock_timestamp(), 
				nm_usuario_p, 
				null, 
				null, 
				nextval('sus_erro_imp_colmeia_seq'));
	 
	end;
end if;
 
commit;
 
end;
