CREATE OR REPLACE PROCEDURE sus_gerar_proc_prenatal ( nr_aih_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_interno_conta_w	bigint;
nr_sequencia_w		bigint;
qt_reg_w		integer;
nr_sequencia_ww		bigint;
nr_prenatal_w		bigint;

 

BEGIN 
 
select	coalesce(max(nr_interno_conta),0), 
	coalesce(max(nr_gestante_pre_natal),0) 
into STRICT	nr_interno_conta_w, 
	nr_prenatal_w 
from	sus_aih 
where	nr_aih		= nr_aih_p 
and	nr_sequencia	= nr_sequencia_p;
 
select	coalesce(min(nr_sequencia),0) 
into STRICT	nr_sequencia_w 
from	procedimento_paciente 
where	nr_interno_conta	= nr_interno_conta_w 
and	ie_origem_proced	= 2 
and	cd_procedimento	in ( 
	35001011,35006013,35007010,35023015,35027010,35025018,35080019, 35021012,35024011, 
	35009012,35022019,35026014,35028017,35082011,35084014,35083018,35085010);
 
select	count(*) 
into STRICT	qt_reg_w 
from	procedimento_paciente 
where	nr_interno_conta	= nr_interno_conta_w 
and	cd_procedimento		= 95002014 
and	ie_origem_proced	= 2;
 
if (nr_sequencia_w	> 0) and (qt_reg_w	= 0) and (nr_prenatal_w	> 0) then 
	nr_sequencia_ww := Duplicar_proc_paciente(nr_sequencia_w, nm_usuario_p, nr_sequencia_ww);
	update	procedimento_paciente 
	set	cd_procedimento		= 95002014, 
		cd_medico_executor	 = NULL, 
		ie_tipo_servico_sus	= 25, 
		ie_tipo_ato_sus		= 37 
	where	nr_sequencia		= nr_sequencia_ww;
	CALL Atualiza_Preco_Proc_Sus(nr_sequencia_ww, nm_usuario_p);
end if;
 
commit;
 
END;
