SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_gerar_tabela_proc_pab ( dt_competencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		integer	:= 0;

c_sus_preco_mun CURSOR FOR
	SELECT	b.cd_procedimento,
		b.ie_origem_proced,
		a.vl_sa,
		a.vl_total_hospitalar,
		a.vl_sp,
		a.vl_sh
	from	sus_procedimento	c,
		procedimento		b,
		sus_preco		a
	where	a.cd_procedimento	= b.cd_procedimento
	and	a.ie_origem_proced	= b.ie_origem_proced
	and	b.cd_procedimento	= c.cd_procedimento
	and	b.ie_origem_proced	= c.ie_origem_proced
	and	((c.ie_tipo_financiamento	= 'AB') or (c.ie_tipo_financiamento	= '01'));


BEGIN

for r_sus_preco_mun in c_sus_preco_mun loop
	begin

	select	count(*)
	into STRICT	qt_registros_w
	from	sus_preco_municipio
	where	cd_procedimento		= r_sus_preco_mun.cd_procedimento
	and	ie_origem_proced	= r_sus_preco_mun.ie_origem_proced
	and	dt_competencia		= dt_competencia_p
	and	coalesce(cd_municipio_ibge,'X') = 'X';

	if (qt_registros_w	= 0) then
		insert into sus_preco_municipio(nr_sequencia,
			dt_competencia,
			vl_sa,
			vl_total_hospitalar,
			vl_sp,
			vl_sh,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_municipio_ibge,
			cd_procedimento,
			ie_origem_proced)
		values (nextval('sus_preco_municipio_seq'),
			dt_competencia_p,
			r_sus_preco_mun.vl_sa,
			r_sus_preco_mun.vl_total_hospitalar,
			r_sus_preco_mun.vl_sp,
			r_sus_preco_mun.vl_sh,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			'',
			r_sus_preco_mun.cd_procedimento,
			r_sus_preco_mun.ie_origem_proced);
	end if;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
