CREATE OR REPLACE PROCEDURE sus_gravar_motivo_nexec_laudo ( nr_seq_interno_p bigint, nr_seq_motivo_nexec_proc_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
BEGIN
 
if (coalesce(nr_seq_motivo_nexec_proc_p,0) <> 0) then 
	begin 
	 
	update	sus_laudo_paciente 
	set	nr_seq_motivo_nexec_proc	= nr_seq_motivo_nexec_proc_p, 
		ds_observacao		= substr(ds_observacao_p,1,2000), 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp() 
	where	nr_seq_interno		= nr_seq_interno_p;
	 
	insert into sus_laudo_paciente_hist( 
				nr_sequencia, 
				dt_atualizacao, 
				nm_usuario, 
				nr_seq_laudo, 
				dt_historico, 
				ds_historico, 
				cd_pessoa_fisica, 
				dt_atualizacao_nrec, 
				nm_usuario_nrec) 
			values ( nextval('sus_laudo_paciente_hist_seq'), 
				clock_timestamp(), 
				nm_usuario_p, 
				nr_seq_interno_p, 
				clock_timestamp(), 
				substr('Informado o motivo da não execução do laudo.',1,2000), 
				Obter_Dados_Usuario_Opcao(nm_usuario_p,'C'), 
				clock_timestamp(), 
				nm_usuario_p);
	 
	commit;
	 
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
