SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_gravar_nr_controle ( nr_controle_sus_p text, nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint := 0;


BEGIN

if (coalesce(nr_controle_sus_p,'X') <> 'X') then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	sus_dados_aih_conta
	where	nr_interno_conta = nr_interno_conta_p;

	if (nr_sequencia_w > 0) then
		begin

		update	sus_dados_aih_conta
		set	nr_controle_sus = nr_controle_sus_p,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia 	= nr_sequencia_w;

		end;
	else
		begin

		select	nextval('sus_dados_aih_conta_seq')
		into STRICT	nr_sequencia_w
		;

		insert into sus_dados_aih_conta(
			nr_sequencia,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nr_interno_conta,
			nr_controle_sus,
			cd_motivo_cobranca,
			ie_codigo_autorizacao)
		values (	nr_sequencia_w,
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			nr_interno_conta_p,
			nr_controle_sus_p,
			null,
			null);

		end;
	end if;

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
