CREATE OR REPLACE PROCEDURE sus_import_cid_especialidade ( ie_tipo_import_p text, dt_importacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE

					 
cd_categ_final_w		cid_especialidade.cd_categ_final%type;
cd_categ_inicial_w		cid_especialidade.cd_categ_inicial%type;
cd_especialidade_cid_w		cid_especialidade.cd_especialidade_cid%type;
ds_especialidade_cid_w		cid_especialidade.ds_especialidade_cid%type;
ds_especialidade_compl_w	cid_especialidade.ds_especialidade_compl%type;
qt_espec_w			bigint := 0;

C01 CURSOR FOR 
	SELECT	substr(obter_valor_campo_separador(ds_conteudo,1,';'),1,10) cd_especialidade_cid, 
		substr(obter_valor_campo_separador(ds_conteudo,2,';'),1,10) cd_categ_inicial, 
		substr(obter_valor_campo_separador(ds_conteudo,3,';'),1,10) cd_categ_final, 
		substr(obter_valor_campo_separador(ds_conteudo,4,';'),1,100) ds_especialidade_cid, 
		substr(obter_valor_campo_separador(ds_conteudo,5,';'),1,2000) ds_especialidade_compl 
	from	w_sus_importacao_cid 
	where	nm_usuario		= nm_usuario_p 
	and	ie_tipo_importacao		= ie_tipo_import_p 
	and	trunc(dt_importacao)	= trunc(dt_importacao_p) 
	order by	ds_conteudo;
	
type 		fetch_array is table of c01%rowtype;
s_array 	fetch_array;
i		integer := 1;
type vetor is table of fetch_array index by integer;
vetor_c01_w			vetor;
					
BEGIN 
 
open c01;
loop 
fetch c01 bulk collect into s_array limit 100000;
	vetor_c01_w(i) := s_array;
	i := i + 1;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;
 
for i in 1..vetor_c01_w.count loop 
	begin 
	s_array := vetor_c01_w(i);
	for z in 1..s_array.count loop 
		begin 
		 
		cd_especialidade_cid_w		:= s_array[z].cd_especialidade_cid;
		cd_categ_inicial_w		:= s_array[z].cd_categ_inicial;
		cd_categ_final_w		:= s_array[z].cd_categ_final;
		ds_especialidade_compl_w	:= s_array[z].ds_especialidade_compl;
		ds_especialidade_cid_w		:= s_array[z].ds_especialidade_cid;
		 
		if (cd_especialidade_cid_w <> 'NUMCAP') then 
			begin 
			 
			select 	count(1) 
			into STRICT	qt_espec_w 
			from 	cid_especialidade 
			where	cd_especialidade_cid = cd_especialidade_cid_w  LIMIT 1;
		 
			if (qt_espec_w = 0) then 
				begin 
				insert into cid_especialidade( 
					cd_especialidade_cid, 
					cd_categ_inicial, 
					cd_categ_final, 
					ds_especialidade_compl, 
					ds_especialidade_cid, 
					dt_atualizacao, 
					dt_atualizacao_nrec, 
					nm_usuario, 
					nm_usuario_nrec) 
				values (	cd_especialidade_cid_w, 
					cd_categ_inicial_w, 
					cd_categ_final_w, 
					ds_especialidade_compl_w, 
					ds_especialidade_cid_w, 
					clock_timestamp(), 
					clock_timestamp(), 
					nm_usuario_p, 
					nm_usuario_p);			
				end;
			else 
				begin 
				 
				update 	cid_especialidade 
				set 	cd_categ_inicial	= cd_categ_inicial_w, 
					cd_categ_final		= cd_categ_final_w, 
					ds_especialidade_compl	= ds_especialidade_compl_w, 
					ds_especialidade_cid	= ds_especialidade_cid_w, 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	cd_especialidade_cid 	= cd_especialidade_cid_w;
				end;
			end if;
		 
			commit;
			 
			end;
		end if;
		 
		end;
	end loop;
	end;
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
