CREATE OR REPLACE PROCEDURE sus_insere_inf_padrao_laudo ( nr_seq_interno_p bigint, ds_condicao_justifica_p text, ds_sinal_sintoma_p text, ds_result_prova_p text, nr_seq_localizacao_laudo_p bigint, ie_tipo_p text, nm_usuario_p text) AS $body$
BEGIN
 
if (ie_tipo_p = 'I') then 
	begin	 
	update 	sus_laudo_paciente 
	set 	ds_condicao_justifica = substr(ds_condicao_justifica_p,1,2000), 
		ds_sinal_sintoma = substr(ds_sinal_sintoma_p,1,2000), 
		ds_result_prova = substr(ds_result_prova_p,1,2000), 
		nr_seq_localizacao_laudo = nr_seq_localizacao_laudo_p, 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where 	nr_seq_interno = nr_seq_interno_p;	
	end;
elsif (ie_tipo_p = 'A') then 
	begin 
	update 	sus_laudo_paciente 
	set 	ds_condicao_justifica = substr(ds_condicao_justifica_p,1,2000), 
		nr_seq_localizacao_laudo = nr_seq_localizacao_laudo_p, 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where 	nr_seq_interno = nr_seq_interno_p;
	end;
end if;
 
commit;
 
end;
$body$
