CREATE OR REPLACE PROCEDURE sus_inserir_dados_atend_aih ( nr_aih_p text, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_entrada_w		timestamp;
dt_alta_w		timestamp;
cd_motivo_alta_w	smallint;
			

BEGIN         
 
begin 
select	dt_entrada, 
	dt_alta 
into STRICT	dt_entrada_w, 
	dt_alta_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
exception 
	when others then 
	dt_entrada_w		:= null;
	dt_alta_w		:= null;
	end;
 
begin 
select	b.cd_motivo_alta_sus 
into STRICT	cd_motivo_alta_w 
from	atendimento_paciente a, 
	motivo_alta b 
where	a.nr_atendimento = nr_atendimento_p 
and	a.cd_motivo_alta = b.cd_motivo_alta;
exception 
	when others then 
	cd_motivo_alta_w	:= null;
	end;
	 
update 	sus_aih_unif 
set	dt_inicial 		= dt_entrada_w, 
	dt_final		= dt_alta_w, 
	cd_motivo_cobranca	= cd_motivo_alta_w, 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp() 
where	nr_aih		= nr_aih_p 
and	nr_atendimento 	= nr_atendimento_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
