SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_insert_detalhe_proc (cd_procedimento_p bigint, nr_seq_detalhe_p bigint, nm_usuario_p text, dt_inicial_p text, dt_final_p text) AS $body$
DECLARE


dt_inicial_w	timestamp;
dt_final_w		timestamp;


BEGIN

begin
dt_inicial_w := to_date(dt_inicial_p,'yyyymm');
exception
when others then
	dt_inicial_w := null;
end;

begin
update	sus_detalhe_proc
set	dt_compt_fim 	= (last_day(dt_compt_ini)+(86399/86400)),
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	cd_procedimento	= cd_procedimento_p
and	ie_origem_proced = 7
and	coalesce(dt_compt_fim::text, '') = ''
and	trunc(dt_compt_ini) < trunc(dt_inicial_w);
exception
	when others then
	dt_inicial_w := dt_inicial_w;
	end;

if (dt_final_p = 999999) then
	dt_final_w := null;
else
	begin
	dt_final_w := (last_day(to_date(dt_final_p,'yyyymm'))+(86399/86400));
	exception
	when others then
		dt_final_w := null;
	end;
end if;

insert 	into sus_detalhe_proc(cd_procedimento,
	ie_origem_proced,
	nr_seq_detalhe,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_sequencia,
	dt_compt_ini,
	dt_compt_fim)
values (cd_procedimento_p,
	7,
	nr_seq_detalhe_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nextval('sus_detalhe_proc_seq'),
	dt_inicial_w,
	dt_final_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
