CREATE OR REPLACE PROCEDURE sus_insert_espec_leito (cd_espec_leito_p bigint, ds_espec_leito_p text, nr_sequencia_p bigint, nr_seq_leito_p bigint, cd_tipo_leito_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_leito_w  sus_espec_leito.nr_seq_leito%type;


BEGIN

begin
select  nr_sequencia
into STRICT    nr_seq_leito_w
from    sus_tipo_leito_unif
where   cd_tipo_leito = cd_espec_leito_p;
exception
when others then
        nr_seq_leito_w := 0;
end;

if (coalesce(nr_seq_leito_w,0) > 0) then
        begin
        insert 	into sus_espec_leito(cd_espec_leito,
                ds_espec_leito, 
                dt_atualizacao, 
                nm_usuario, 
                dt_atualizacao_nrec, 
                nm_usuario_nrec, 
                nr_sequencia, 
                nr_seq_leito, 
                cd_tipo_leito)
        values (cd_espec_leito_p, 
                ds_espec_leito_p, 
                clock_timestamp(), 
                nm_usuario_p, 
                clock_timestamp(), 
                nm_usuario_p, 
                nextval('sus_espec_leito_seq'), 
                nr_seq_leito_w, 
                cd_espec_leito_p);
        end;
end if;

commit;

end;
$body$
