CREATE OR REPLACE PROCEDURE sus_laudo_gravar_inco ( nr_seq_laudo_p bigint, cd_inconsistencia_p bigint, ds_detalhe_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_inco_w		bigint;
nr_sequencia_w		bigint;



BEGIN

select	nr_sequencia
into STRICT	nr_seq_inco_w
from	sus_inconsistencia_laudo
where	cd_inconsistencia	= cd_inconsistencia_p;

select	coalesce(max(nr_sequencia),0) + 1
into STRICT	nr_sequencia_w
from	sus_inco_reg_laudo
where	nr_seq_laudo	= nr_seq_laudo_p;

begin
insert into sus_inco_reg_laudo(nr_seq_laudo,
		nr_sequencia,
		nr_seq_inconsistencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_detalhe)
	values (	nr_seq_laudo_p,
		nr_sequencia_w,
		nr_seq_inco_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_detalhe_p);
exception
when others then
	--ra.ise_application_error(-20011,'Não foi possível gravar a inconsistência ' || cd_inconsistencia_p || ' do laudo ' || nr_seq_laudo_p || ' #@#@');
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263524,'cd_inconsistencia_p='||cd_inconsistencia_p||';'||
							'nr_seq_laudo_p='||nr_seq_laudo_p);
end;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
