CREATE OR REPLACE PROCEDURE sus_limpar_import_rubrica ( nr_seq_protocolo_p bigint) AS $body$
DECLARE



qt_pagas_w		integer;
qt_rejeitadas_w		integer;



BEGIN

select	count(*)
into STRICT	qt_pagas_w
from	sus_aih_paga
where	nr_Seq_protocolo	= nr_seq_protocolo_p;

if (qt_pagas_w	> 0) then
	delete	FROM sus_aih_paga
	where	nr_seq_protocolo	= nr_seq_protocolo_p;
end if;

select	count(*)
into STRICT	qt_rejeitadas_w
from	sus_aih_rejeitada
where	nr_seq_protocolo	= nr_seq_protocolo_p;

if (qt_rejeitadas_w	> 0) then
	delete	FROM sus_aih_rejeitada
	where	nr_seq_protocolo	= nr_seq_protocolo_p;
end if;

delete	FROM sus_aih_processo
where	nr_seq_protocolo	= nr_Seq_protocolo_p;

commit;

END;
