SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_limpar_proc_dif_aih ( NR_INTERNO_CONTA_P bigint, NM_USUARIO_P text) AS $body$
DECLARE


nr_sequencia_w			bigint	:= 0;
nr_interno_conta_w		bigint	:= 0;
nr_atendimento_w		bigint	:= 0;
cd_convenio_w			integer	:= 0;
ie_tipo_protocolo_w		smallint	:= 0;
ie_status_protocolo_w		smallint	:= 0;
ie_tipo_convenio_w		smallint	:= 0;


C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_interno_conta,
		a.nr_atendimento
	from	procedimento_paciente a
	where	a.nr_interno_conta	= nr_interno_conta_p
	and	a.ie_origem_proced	<> 2
	and	a.ie_origem_proced	<> 7;


BEGIN

OPEN C01;
LOOP
	FETCH 	C01 into
		nr_sequencia_w,
		nr_interno_conta_w,
		nr_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	BEGIN

	/* deletar registros da tabela com valores originais do sus */

	begin
	delete	from sus_valor_proc_paciente
	where	nr_sequencia	= nr_sequencia_w;
	exception
		when others then
		nr_sequencia_w	:= nr_sequencia_w;
	end;

	/* Zerar valores de procedimentos diferentes da AIH */

	begin
	update	procedimento_paciente
	set	vl_procedimento		= 0,
		vl_medico		= 0,
		vl_anestesista		= 0,
		vl_materiais		= 0,
		vl_auxiliares		= 0,
		vl_custo_operacional	= 0
	where	nr_sequencia		= nr_sequencia_w;
	exception
		when others then
		nr_sequencia_w	:= nr_sequencia_w;
	end;

	/* Zerar valores dos participantes com procedimentos diferentes da AIH */

	begin
	update	procedimento_participante
	set	vl_participante		= 0,
		vl_conta		= 0,
		vl_original		= 0
	where	nr_sequencia		= nr_sequencia_w;
	exception
		when others then
		nr_sequencia_w	:= nr_sequencia_w;
	end;

	END;
END LOOP;
CLOSE C01;
if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
