SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_mudanca_proc_conta ( nr_interno_conta_p bigint, cd_proc_original_p bigint, cd_procedimento_solic_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
nr_sequencia_w   	bigint;
ie_status_acerto_w	smallint;


BEGIN 
 
begin 
select	max(nr_sequencia) 
into STRICT	nr_sequencia_w 
from	procedimento_paciente 
where	nr_interno_conta = nr_interno_conta_p 
and	cd_procedimento	 = cd_proc_original_p;
exception 
	when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(233306,'cd_proc_original_p='||cd_proc_original_p||';nr_interno_conta_p='||nr_interno_conta_p);
	end;
 
if (coalesce(nr_interno_conta_p,0) <> 0) and (coalesce(cd_proc_original_p,0) <> 0) and (coalesce(cd_procedimento_solic_p,0) <> 0) and (coalesce(nr_sequencia_w,0) = 0) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(233306,'cd_proc_original_p='||cd_proc_original_p||';nr_interno_conta_p='||nr_interno_conta_p);
	end if;	
	 
select	max(ie_status_acerto) 
into STRICT	ie_status_acerto_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_p;
 
 
if (coalesce(nr_sequencia_w,0) <> 0) and (ie_status_acerto_w = 1) then 
	begin	 
	 
	Update 	procedimento_paciente 
	set	cd_procedimento = cd_procedimento_solic_p, 
		dt_atualizacao	= clock_timestamp(), 
		nm_usuario	= nm_usuario_p 
	where	nr_sequencia	= nr_sequencia_w;
	 
	commit;
 
	CALL sus_atualiza_valor_proc(nr_sequencia_w,nm_usuario_p);
	 
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
