SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_obter_carater_uf ( cd_pessoa_fisica_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint, ie_funcao_p bigint, ie_carater_p INOUT text) AS $body$
DECLARE



/* ie_funcao_p
	1 - aih atual
	2 - aih unificada
*/
sg_uf_w			compl_pessoa_fisica.sg_estado%type;
ie_carater_inter_sus_w	varchar(2);
cd_carater_internacao_w	varchar(2);
ie_complexidade_w		varchar(2);



BEGIN

select	max(coalesce(sg_estado,''))
into STRICT	sg_uf_w
from 	compl_pessoa_fisica
where 	cd_pessoa_fisica = cd_pessoa_fisica_p
and 	ie_tipo_complemento = 1;

begin
select	coalesce(ie_complexidade,'0')
into STRICT	ie_complexidade_w
from	sus_procedimento
where	cd_procedimento = cd_procedimento_p
and	ie_origem_proced = ie_origem_proced_p;
exception
	when others then
	ie_complexidade_w := '0';
end;

if (sg_uf_w IS NOT NULL AND sg_uf_w::text <> '') then
	begin
	select	ie_carater_inter_sus,
		cd_carater_internacao
	into STRICT	ie_carater_inter_sus_w,
		cd_carater_internacao_w
	from	sus_carater_uf
	where	coalesce(cd_procedimento,cd_procedimento_p) = cd_procedimento_p
	and	coalesce(ie_origem_proced,ie_origem_proced_p) = ie_origem_proced_p
	and	coalesce(ie_complexidade,ie_complexidade_w) = ie_complexidade_w
	and	((cd_unidade_federacao	= sg_uf_w)
	or 	((coalesce(cd_unidade_federacao::text, '') = '') and not exists (	SELECT	1
	   							from	sus_carater_uf
								where	coalesce(cd_procedimento,cd_procedimento_p) = cd_procedimento_p
								and	coalesce(ie_origem_proced,ie_origem_proced_p) = ie_origem_proced_p
								and	coalesce(ie_complexidade,ie_complexidade_w) = ie_complexidade_w
								and	cd_unidade_federacao	= sg_uf_w)))
	and	cd_estabelecimento	= cd_estabelecimento_p;
	exception
		when others then
		ie_carater_inter_sus_w	:= '';
		cd_carater_internacao_w	:= '';
	end;

end if;

if (ie_funcao_p	= 1) then
	ie_carater_p	:= ie_carater_inter_sus_w;
else
	ie_carater_p	:= cd_carater_internacao_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
