CREATE OR REPLACE PROCEDURE sus_obter_exec_exp_aih ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_medico_executor_p text, cd_cbo_p text, cd_estabelecimento_p bigint, ie_aih_p text, ie_bpa_p text, ie_apac_p text, cd_medico_exec_exp_p INOUT text, cd_cbo_exp_p INOUT text) AS $body$
DECLARE


ie_exclui_medico_w		varchar(1)	:= 'N';
ie_exclui_cnes_w		varchar(1)	:= 'N';
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
nr_seq_grupo_w		bigint;
nr_seq_subgrupo_w	bigint;
nr_seq_forma_org_w	bigint;
cd_estabelecimento_w	smallint;
ie_credenciamento_w	varchar(3);
cd_medico_executor_w	varchar(10);
cd_pessoa_fisica_w	varchar(10);
cd_profissional_w		varchar(10);
