	cd_medico_executor_w	:= cd_medico_executor_w;
	cd_cbo_executor_w	:= cd_cbo_executor_w;
	end;
end loop;
close C01;

cd_medico_exec_exp_p	:= cd_medico_executor_w;
cd_cbo_exp_p		:= cd_cbo_executor_w;

end;
