CREATE OR REPLACE PROCEDURE sus_obter_procedimento_amb ( cd_procedimento_p bigint , ie_origem_proced_p bigint , ie_opcao_p bigint , cd_proc_convetido_p INOUT bigint , ie_origem_convetida_p INOUT bigint ) AS $body$
DECLARE


cd_procedimento_ww	bigint;
cd_procedimento_w	bigint;
ie_origem_proced_ww	bigint;
ie_origem_proced_w	bigint;

/*ie_opcao_p:
1 - Convênio p/ SUS
2 - SUS p/ Convênio
*/
C01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced
	from	procedimento_conv_sus
	where	cd_procedimento_sus	= cd_procedimento_p
	and	ie_origem_proced_sus	= ie_origem_proced_p
	order by 1;


BEGIN

if (ie_opcao_p = 1) then
	begin
	select	max(cd_procedimento_sus),
		max(ie_origem_proced_sus)
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from	procedimento_conv_sus
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced		= ie_origem_proced_p;
	end;
else
	begin
	open C01;
	loop
	fetch C01 into
		cd_procedimento_ww,
		ie_origem_proced_ww;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		cd_procedimento_w		:= cd_procedimento_ww;
		ie_origem_proced_w	:= ie_origem_proced_ww;
		end;
	end loop;
	close C01;
	end;
end if;

cd_proc_convetido_p	:= coalesce(cd_procedimento_w,cd_procedimento_p);
ie_origem_convetida_p	:= coalesce(ie_origem_proced_w,ie_origem_proced_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
