SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_obter_regra_cirur_eletiva (nr_interno_conta_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_procedimento_p timestamp, ie_tipo_atendimento_p bigint, vl_sh_p INOUT bigint, vl_sp_p INOUT bigint, pr_acrescimo_p INOUT bigint, ie_ajusta_tipo_fin_p INOUT text) AS $body$
DECLARE


vl_sh_w			sus_regra_cirur_elet_proc.vl_sh%type := 0;
vl_sp_w			sus_regra_cirur_elet_proc.vl_sp%type := 0;
pr_acrescimo_w		sus_regra_cirur_elet_proc.pr_acrescimo%type := 0;
nr_aih_w			sus_aih_unif.nr_aih%type;
nr_apac_w		sus_apac_unif.nr_apac%type;
nr_seq_regra_w		sus_regra_cirurgia_eletiva.nr_sequencia%type;
ie_ajusta_tipo_fin_w	sus_regra_cirurgia_eletiva.ie_ajusta_tipo_fin_aih%type;
					

BEGIN

if (ie_tipo_atendimento_p = 1) then
	begin
	
	select	coalesce(max(nr_aih),0)
	into STRICT	nr_aih_w
	from	sus_aih_unif
	where	nr_interno_conta = nr_interno_conta_p;
	
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_regra_w
	from	sus_regra_cirurgia_eletiva
	where	ie_situacao = 'A'
	and	nr_aih_w between coalesce(nr_aih_inicial,0) and coalesce(nr_aih_final,0);
	
	end;
	
	if (coalesce(nr_seq_regra_w,0) > 0) then
		begin
			select 	a.ie_ajusta_tipo_fin_aih
			into STRICT	ie_ajusta_tipo_fin_w
			from	sus_regra_cirurgia_eletiva a
			where	a.nr_sequencia = nr_seq_regra_w;
				exception
					when others then
							ie_ajusta_tipo_fin_w := 'N';
		end;
	end if;	
else
	begin
	
	select	coalesce(max(nr_apac),0)
	into STRICT	nr_apac_w
	from	sus_apac_unif
	where	nr_interno_conta = nr_interno_conta_p;
	
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_regra_w
	from	sus_regra_cirurgia_eletiva
	where	ie_situacao = 'A'
	and	nr_apac_w between coalesce(nr_apac_inicial,0) and coalesce(nr_apac_final,0);
	
	end;
end if;

if (coalesce(nr_seq_regra_w,0) > 0) then
	begin
	
	begin
	select	coalesce(vl_sh,0),
		coalesce(vl_sp,0),
		coalesce(pr_acrescimo,0)
	into STRICT	vl_sh_w,
		vl_sp_w,
		pr_acrescimo_w
	from	sus_regra_cirur_elet_proc
	where	ie_situacao 		= 'A'
	and	nr_seq_regra_cir_elet 	= nr_seq_regra_w
	and	cd_procedimento 	= cd_procedimento_p
	and	ie_origem_proced 	= ie_origem_proced_p
	and	dt_procedimento_p between dt_inicio_vigencia and dt_fim_vigencia;	
	exception
	when others then
		vl_sh_w		:= 0;
		vl_sp_w		:= 0;
		pr_acrescimo_w	:= 0;
	end;
	
	end;
end if;

vl_sh_p		:= vl_sh_w;
vl_sp_p		:= vl_sp_w;
pr_acrescimo_p	:= pr_acrescimo_w;
ie_ajusta_tipo_fin_p := coalesce(ie_ajusta_tipo_fin_w, 'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
