CREATE OR REPLACE PROCEDURE sus_obter_total_proced_esus ( cd_profissional_p text, dt_atendimento_p timestamp, qt_afericao_pa_p INOUT bigint, qt_afericao_temperat_p INOUT bigint, qt_col_mate_exam_lab_p INOUT bigint, qt_curativo_simples_p INOUT bigint, qt_glicemia_capilar_p INOUT bigint, qt_medicao_altura_p INOUT bigint, qt_medicao_peso_p INOUT bigint, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_afericao_pa_w			esus_ficha_proc_profis.qt_afericao_pa%type := 0;
qt_afericao_temperat_w		esus_ficha_proc_profis.qt_afericao_temperat%type := 0;
qt_col_mate_exam_lab_w		esus_ficha_proc_profis.qt_col_mate_exam_lab%type := 0;
qt_curativo_simples_w		esus_ficha_proc_profis.qt_curativo_simples%type := 0;
qt_glicemia_capilar_w		esus_ficha_proc_profis.qt_glicemia_capilar%type := 0;
qt_medicao_altura_w		esus_ficha_proc_profis.qt_medicao_altura%type := 0;
qt_medicao_peso_w		esus_ficha_proc_profis.qt_medicao_peso%type := 0;
nr_sequencia_w			esus_regra_total_campo.nr_sequencia%type;
ie_campo_totalizador_w		esus_regra_total_campo.ie_campo_totalizador%type;
cd_procedimento_w		esus_regra_total_proced.cd_procedimento%type;
ie_origem_proced_w		esus_regra_total_proced.ie_origem_proced%type;
qt_proced_campo_w		bigint := 0;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_campo_totalizador
	from	esus_regra_total_campo
	where	ie_situacao = 'A'
	order by ie_campo_totalizador;

C02 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced
	from	esus_regra_total_proced
	where	nr_seq_regra_campo = nr_sequencia_w
	order by cd_procedimento;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	ie_campo_totalizador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		cd_procedimento_w,
		ie_origem_proced_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	coalesce(sum(qt_procedimento),0)
		into STRICT	qt_proced_campo_w
		from	procedimento_paciente
		where	cd_procedimento = cd_procedimento_w
		and	ie_origem_proced = ie_origem_proced_w
		and	coalesce(cd_pessoa_fisica,cd_medico_executor) = cd_profissional_p
		and	coalesce(cd_motivo_exc_conta::text, '') = ''
		and	trunc(dt_procedimento) = trunc(dt_atendimento_p);

		if (ie_campo_totalizador_w = '1') then
			begin
			qt_afericao_pa_w := qt_afericao_pa_w + qt_proced_campo_w;
			end;
		elsif (ie_campo_totalizador_w = '2') then
			begin
			qt_afericao_temperat_w := qt_afericao_temperat_w + qt_proced_campo_w;
			end;
		elsif (ie_campo_totalizador_w = '3') then
			begin
			qt_curativo_simples_w := qt_curativo_simples_w + qt_proced_campo_w;
			end;
		elsif (ie_campo_totalizador_w = '4') then
			begin
			qt_col_mate_exam_lab_w := qt_col_mate_exam_lab_w + qt_proced_campo_w;
			end;
		elsif (ie_campo_totalizador_w = '5') then
			begin
			qt_glicemia_capilar_w := qt_glicemia_capilar_w + qt_proced_campo_w;
			end;
		elsif (ie_campo_totalizador_w = '6') then
			begin
			qt_medicao_altura_w := qt_medicao_altura_w + qt_proced_campo_w;
			end;
		elsif (ie_campo_totalizador_w = '7') then
			begin
			qt_medicao_peso_w :=  qt_medicao_peso_w + qt_proced_campo_w;
			end;
		end if;

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

qt_afericao_pa_p		:= qt_afericao_pa_w;
qt_afericao_temperat_p	:= qt_afericao_temperat_w;
qt_col_mate_exam_lab_p	:= qt_col_mate_exam_lab_w;
qt_curativo_simples_p	:= qt_curativo_simples_w;
qt_glicemia_capilar_p	:= qt_glicemia_capilar_w;
qt_medicao_altura_p	:= qt_medicao_altura_w;
qt_medicao_peso_p	:= qt_medicao_peso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
