CREATE OR REPLACE PROCEDURE sus_rateio_sh_aux ( nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_propaci_w	bigint;
nr_seq_matpaci_w	bigint;
cd_item_w		bigint;
ie_origem_proced_w	bigint;
qt_item_w		double precision;
vl_item_w		double precision;
cd_setor_atendimento_w	integer;
dt_item_w		timestamp;

c01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_procedimento,
		ie_origem_proced,
		qt_procedimento,
		vl_materiais,
		cd_setor_atendimento,
		dt_procedimento
	from	procedimento_paciente
	where	nr_interno_conta	= nr_interno_conta_p
	and	coalesce(cd_motivo_exc_conta::text, '') = '';

c02 CURSOR FOR
	SELECT	nr_sequencia,
		cd_material,
		qt_material,
		0,
		cd_setor_atendimento,
		dt_atendimento
	from	material_atend_paciente
	where	nr_interno_conta	= nr_interno_conta_p
	and	coalesce(cd_motivo_exc_conta::text, '') = '';


BEGIN

/* Gerar procedimentos */

OPEN C01;
LOOP
FETCH 	C01 into
	nr_seq_propaci_w,
	cd_item_w,
	ie_origem_proced_w,
	qt_item_w,
	vl_item_w,
	cd_setor_atendimento_w,
	dt_item_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	nextval('w_conta_sus_sh_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into w_conta_sus_sh(
		NR_SEQUENCIA,
		NR_INTERNO_CONTA,
		CD_ITEM,
		IE_ORIGEM_PROCED,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		VL_PRECO,
		VL_RECEITA,
		QT_ITEM,
		NR_SEQ_PROPACI,
		NR_SEQ_MATPACI,
		cd_setor_atendimento,
		dt_item,
		vl_original)
	values (nr_sequencia_w,
		nr_interno_conta_p,
		cd_item_w,
		ie_origem_proced_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		0,
		0,
		qt_item_w,
		nr_seq_propaci_w,
		null,
		cd_setor_atendimento_w,
		dt_item_w,
		vl_item_w);
	end;
END LOOP;
CLOSE C01;


/* Gerar matmed */

OPEN C02;
LOOP
FETCH 	C02 into
	nr_seq_matpaci_w,
	cd_item_w,
	qt_item_w,
	vl_item_w,
	cd_setor_atendimento_w,
	dt_item_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
	begin
	select	nextval('w_conta_sus_sh_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into w_conta_sus_sh(
		NR_SEQUENCIA,
		NR_INTERNO_CONTA,
		CD_ITEM,
		IE_ORIGEM_PROCED,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		VL_PRECO,
		VL_RECEITA,
		QT_ITEM,
		NR_SEQ_PROPACI,
		NR_SEQ_MATPACI,
		cd_setor_atendimento,
		dt_item)
	values (nr_sequencia_w,
		nr_interno_conta_p,
		cd_item_w,
		0,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		0,
		vl_item_w,
		qt_item_w,
		null,
		nr_seq_matpaci_w,
		cd_setor_atendimento_w,
		dt_item_w);
	end;
END LOOP;
CLOSE C02;


commit;


END;
$body$
