CREATE OR REPLACE PROCEDURE sus_recalc_contas_atend_apac ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_interno_conta_w conta_paciente.nr_interno_conta%type;

C01 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_atendimento = nr_atendimento_p 
	and	ie_status_acerto = 1 
	order by nr_interno_conta;
	

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	CALL recalcular_conta_paciente(nr_interno_conta_w,nm_usuario_p);
	 
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
