SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_transfere_laudo_aih ( nr_atendimento_p text, cd_estabelecimento_p text, nm_usuario_p text) AS $body$
DECLARE


			

ie_vincula_laudo_w		varchar(15) := 'N';
ie_vincula_laudo_ext_w		varchar(15) := 'N';
cd_pessoa_fisica_w		varchar(10);
qt_laudo_w			bigint;
nr_seq_interno_w		bigint;
ie_gera_aih_w			varchar(15) := 'N';
ie_vincula_laudo_ps_w		varchar(15) := 'N';
ie_transf_laudo_sem_lib_w	SUS_PARAMETROS_AIH.ie_transf_laudo_sem_lib%type;
nr_laudo_sus_w              sus_laudo_paciente.nr_laudo_sus%type;


BEGIN

select	coalesce(max(ie_vincular_laudo_tipo_atend),'N'),
	coalesce(max(ie_vincular_laudo_atend_ext),'N'),
	coalesce(max(ie_gera_aih_laudo_tranf),'N'),
	coalesce(max(ie_vincular_laudo_atend_ps),'N'),
	coalesce(max(ie_transf_laudo_sem_lib),'N')
into STRICT	ie_vincula_laudo_w,
	ie_vincula_laudo_ext_w,
	ie_gera_aih_w,
	ie_vincula_laudo_ps_w,
	ie_transf_laudo_sem_lib_w
from	sus_parametros_aih
where	cd_estabelecimento = cd_estabelecimento_p;

if (ie_vincula_laudo_w = 'S') or (ie_vincula_laudo_ext_w = 'S') then
	
	select	cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from	atendimento_paciente
	where	nr_atendimento = nr_atendimento_p;
	
	select	count(*)
	into STRICT	qt_laudo_w
	from	sus_laudo_paciente a,
		atendimento_paciente b
	where	a.nr_atendimento = b.nr_atendimento	
	and	b.cd_pessoa_fisica = cd_pessoa_fisica_w
	and	((ie_vincula_laudo_w = 'S' AND b.ie_tipo_atendimento = 8) or
		(ie_vincula_laudo_ext_w = 'S' AND b.ie_tipo_atendimento = 7) or
		(ie_vincula_laudo_ps_w = 'S' AND b.ie_tipo_atendimento = 3))
	and	a.ie_classificacao = 1;
	
	if (qt_laudo_w > 0) then
	
		select	coalesce(max(a.nr_seq_interno),0)
		into STRICT	nr_seq_interno_w
		from	sus_laudo_paciente a,
			atendimento_paciente b
		where	a.nr_atendimento = b.nr_atendimento	
		and	b.cd_pessoa_fisica = cd_pessoa_fisica_w
		and	((ie_vincula_laudo_w = 'S' AND b.ie_tipo_atendimento = 8) or
			(ie_vincula_laudo_ext_w = 'S' AND b.ie_tipo_atendimento = 7) or
			(ie_vincula_laudo_ps_w = 'S' AND b.ie_tipo_atendimento = 3))
		and	a.ie_classificacao = 1;
		
        select  coalesce(max(nr_laudo_sus),0)
        into STRICT	nr_laudo_sus_w
        from    sus_laudo_paciente
        where   nr_atendimento = nr_atendimento_p;

		if (nr_seq_interno_w > 0) then
			begin
			update	sus_laudo_paciente
			set	nr_atendimento = nr_atendimento_p,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp(),
				nr_interno_conta  = NULL,
				dt_liberacao = CASE WHEN ie_transf_laudo_sem_lib_w='S' THEN  null  ELSE dt_liberacao END ,
                nr_laudo_sus = (nr_laudo_sus_w + 1)
			where	nr_seq_interno = nr_seq_interno_w;
			
			if (ie_gera_aih_w = 'S') then
				CALL sus_gerar_aih_liberar_laudo(nr_seq_interno_w,nm_usuario_p);
			end if;	
			
			if (ie_transf_laudo_sem_lib_w = 'N') and (coalesce(nr_atendimento_p,0) > 0)then
				begin
				CALL gerar_lancamento_automatico(nr_atendimento_p,0,536,nm_usuario_p,0,nr_seq_interno_w,'','','',0);				
				end;
			end if;
			
			end;
		end if;	
		
	end if;
	
end if;	

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE sus_transfere_laudo_aih ( nr_atendimento_p text, cd_estabelecimento_p text, nm_usuario_p text) FROM PUBLIC;

