CREATE OR REPLACE PROCEDURE sus_transfere_laudo_atend ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_atend_origem_w	bigint;
cd_pessoa_fisica_w	varchar(10);
nr_seq_interno_w	bigint;
dt_entrada_w		timestamp;


BEGIN 
 
select	max(cd_pessoa_fisica), 
	max(dt_entrada) 
into STRICT	cd_pessoa_fisica_w, 
	dt_entrada_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
select 	max(nr_atendimento) 
into STRICT	nr_atend_origem_w 
from	atendimento_paciente 
where	cd_pessoa_fisica = cd_pessoa_fisica_w 
and	ie_tipo_atendimento = 8 
and	dt_entrada >= (dt_entrada_w -30);
 
 
select	max(nr_seq_interno) 
into STRICT	nr_seq_interno_w 
from	sus_laudo_paciente 
where	nr_atendimento = nr_atend_origem_w 
and	ie_classificacao = 1 
and	dt_emissao >= (clock_timestamp() - interval '30 days') 
and	coalesce(dt_cancelamento::text, '') = '';
 
if (coalesce(nr_seq_interno_w,0) <> 0) then 
	begin 
	update	sus_laudo_paciente 
	set	nr_atendimento = nr_atendimento_p 
	where	nr_seq_interno = nr_seq_interno_w;
	 
	commit;
	end;
end if;		
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
