DECLARE

 
nr_seq_interno_w	bigint;

c01 CURSOR FOR 
	SELECT	nr_seq_interno 
	from	sus_laudo_paciente 
	where 	nr_seq_lote	= nr_lote_origem_p 
	and	((nr_seq_interno = nr_seq_laudo_p) or (coalesce(nr_seq_laudo_p,0) = 0));

-- Colocado o parâmetro nr_seq_laudo_p para fazer multiseleção OS 311060 
BEGIN 
 
open c01;
loop 
fetch c01 into	 
	nr_seq_interno_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	if (coalesce(nr_lote_destino_p,0) <> 0) then 
		begin 
		update	sus_laudo_paciente 
		set	nr_seq_lote 	= nr_lote_destino_p, 
			dt_retorno_secr	 = NULL, 
			nm_usuario	= nm_usuario_p, 
			dt_atualizacao	= clock_timestamp() 
		where	nr_seq_interno	= nr_seq_interno_w;		
		end;
	end if;
	end;
end loop;
close c01;
 
commit;
 
end;
