CREATE OR REPLACE PROCEDURE sus_update_cid_doenca_b (ds_doenca_cid_p text, nm_usuario_p text, cd_doenca_cid_p text, ie_restricao_sexo_p text, ie_subcat_p text) AS $body$
BEGIN

update cid_doenca set
	ds_doenca_cid        	= ds_doenca_cid_p,
	dt_atualizacao       	= clock_timestamp(),
	nm_usuario           	= nm_usuario_p,
	cd_categoria_cid     	= substr(cd_doenca_cid_p,1,3),
	ie_sexo              	= ie_restricao_sexo_p,
	ie_cad_interno       	= 'N',
	ie_situacao          	= 'A',
	ie_subcategoria      	= coalesce(ie_subcat_p,'S')
where 	cd_doenca_cid 		= cd_doenca_cid_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
