CREATE OR REPLACE PROCEDURE sus_update_procedimento_imp (ds_procedimento_p text, cd_grupo_proc_p bigint, nm_usuario_p text, qt_dia_internacao_sus_p bigint, qt_max_procedimento_p bigint, nr_seq_forma_org_p bigint, cd_procedimento_p bigint) AS $body$
DECLARE


qt_form_org_w		bigint;
nr_seq_forma_org_w	sus_forma_organizacao.nr_sequencia%type;


BEGIN

select	count(1)
into STRICT	qt_form_org_w
from	sus_forma_organizacao
where	nr_sequencia = nr_seq_forma_org_p
and	cd_forma_organizacao = substr(cd_procedimento_p, 1, 5);

if ( qt_form_org_w = 0 ) then
	begin
	select	nr_sequencia
	into STRICT	nr_seq_forma_org_w
	from	sus_forma_organizacao
	where	cd_forma_organizacao = substr(cd_procedimento_p, 1, 5);
	end;
else

	nr_seq_forma_org_w	:= nr_seq_forma_org_p;

end if;

update 	procedimento
set	ds_procedimento		= substr(ds_procedimento_p,1,240),
	ie_situacao		= 'A',
	cd_grupo_proc		= cd_grupo_proc_p,         
	dt_atualizacao		= clock_timestamp(),                
	nm_usuario		= nm_usuario_p,            
	ie_classificacao	= 1,      
	qt_dia_internacao_sus	= qt_dia_internacao_sus_p, 
	qt_max_procedimento	= qt_max_procedimento_p,   
	ie_exige_autor_sus	= 'N',    
	qt_exec_barra		= 0,         
	ie_ativ_prof_bpa	= 'N',      
	ie_alta_complexidade	= 'S',  
	ie_ignora_origem	= 'N',      
	nr_seq_forma_org	= nr_seq_forma_org_w,      
	ie_classif_custo	= 'B'       
where	cd_procedimento		= cd_procedimento_p        
and	ie_origem_proced	= 7;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
