CREATE OR REPLACE PROCEDURE sus_update_sus_procedimento (ie_complexidade_p text, qt_idade_minima_p bigint, qt_idade_maxima_p bigint, ie_tipo_financiamento_p text, ie_sexo_p text, nr_seq_forma_org_p bigint, cd_procedimento_p bigint, cd_financiamento_p text default null, cd_rubrica_p text default null, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE


nr_seq_financiamento_w	sus_financiamento.nr_sequencia%type;
nr_seq_rubrica_w	sus_rubrica.nr_sequencia%type;
qt_form_org_w		bigint;
nr_seq_forma_org_w	sus_forma_organizacao.nr_sequencia%type;			
			

BEGIN

select	count(1)
into STRICT	qt_form_org_w
from	sus_forma_organizacao
where	nr_sequencia = nr_seq_forma_org_p
and	cd_forma_organizacao = substr(cd_procedimento_p, 1, 5);

if ( qt_form_org_w = 0 ) then
	begin
	select	nr_sequencia
	into STRICT	nr_seq_forma_org_w
	from	sus_forma_organizacao
	where	cd_forma_organizacao = substr(cd_procedimento_p, 1, 5);
	end;
else

	nr_seq_forma_org_w	:= nr_seq_forma_org_p;

end if;

if (coalesce(cd_financiamento_p,'X') <> 'X') then
	begin
	select	nr_sequencia
	into STRICT	nr_seq_financiamento_w
	from	sus_financiamento
	where	cd_financiamento = cd_financiamento_p  LIMIT 1;
	exception
	when others then
		nr_seq_financiamento_w := 0;
	end;	
end if;	
if (coalesce(cd_rubrica_p, 'X') <> 'X') then	
	begin
	select	nr_sequencia
	into STRICT	nr_seq_rubrica_w
	from	sus_rubrica
	where	cd_rubrica = cd_rubrica_p  LIMIT 1;
	exception
	when others then
		nr_seq_rubrica_w := 0;
	end;
	
end if;	

update sus_procedimento
set      dt_atualizacao         = clock_timestamp(),
nm_usuario             		= nm_usuario_p,             
dt_atualizacao_nrec    		= clock_timestamp(),                 
nm_usuario_nrec        		= nm_usuario_p,        
ie_complexidade        		= ie_complexidade_p,        
qt_idade_minima        		= qt_idade_minima_p,        
qt_idade_maxima        		= qt_idade_maxima_p,        
ie_tipo_financiamento  		= ie_tipo_financiamento_p,  
ie_sexo                			= ie_sexo_p,                
nr_seq_forma_org       		= nr_seq_forma_org_w,
nr_seq_financiamento		= coalesce(nr_seq_financiamento_w, null),
nr_seq_rubrica			= coalesce(nr_seq_rubrica_w, null)        
where    cd_procedimento        = cd_procedimento_p        
and      ie_origem_proced       = 7;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
