CREATE OR REPLACE PROCEDURE sus_verifica_processo ( cd_processo_p bigint, nm_usuario_p text, dt_competencia_p timestamp, dt_apres_p text, nr_seq_protocolo_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_reg_w		integer	:= 0;
nr_seq_protocolo_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	sus_aih_processo
where	cd_processo	= cd_processo_p;

if (qt_reg_w	= 0) then
	insert into sus_aih_processo(
		nr_processo,
		dt_competencia,
		ie_tipo_processo,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
