		cd_estabelecimento,
		ie_classificacao,
		dt_import_paga,
		dt_import_rejeitada,
		dt_import_opm_paga)
	values (nextval('sus_aih_processo_seq'),
		dt_competencia_p,
		'00',
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		dt_apres_p,
		nr_seq_protocolo_p,
		cd_processo_p,
		cd_estabelecimento_p,
		'1',
		null,
		null,
		null);
else
	select	nr_seq_protocolo
	into STRICT	nr_seq_protocolo_w
	from	sus_aih_processo
	where	cd_processo	= cd_processo_p;
	if (nr_seq_protocolo_w	<> nr_seq_protocolo_p) then
		--r.aise_application_error(-20011,'Esse processo já está cadastrado no protocolo: '|| nr_seq_protocolo_w);
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263632,'nr_seq_protocolo_w='||nr_seq_protocolo_w);
	end if;
end if;

