CREATE OR REPLACE PROCEDURE sus_vincular_apac_laudo ( nr_seq_interno_p bigint, nr_apac_p bigint, dt_retorno_secr_p timestamp, dt_inicio_val_p timestamp default null, dt_fim_val_p timestamp default null, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE

 
qt_meses_autorizado_w	integer;
ie_gerar_apac_laudo_w	varchar(1)	:= 'N';
nr_atendimento_w		bigint;
cd_motivo_alta_w		varchar(3);
cd_motivo_cobranca_w	varchar(2);


BEGIN 
 
ie_gerar_apac_laudo_w		:= obter_valor_param_usuario(1006,16,obter_perfil_ativo,nm_usuario_p,0);
 
begin 
update	sus_laudo_paciente 
set	nr_apac			= CASE WHEN nr_apac_p=0 THEN null  ELSE nr_apac_p END , 
	dt_retorno_secr		= dt_retorno_secr_p, 
	dt_inicio_val_apac		= coalesce(dt_inicio_val_p,dt_inicio_val_apac), 
	dt_fim_val_apac		= coalesce(dt_fim_val_p,dt_fim_val_apac), 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp() 
where	nr_seq_interno		= nr_seq_interno_p;
exception 
	when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(221834);
	/*Não foi possível vincular a APAC ao laudo.*/
 
	end;
 
if (ie_gerar_apac_laudo_w = 'S') and (coalesce(nr_apac_p,0) <> 0) then 
	begin 
 
	begin 
	select	max(b.nr_atendimento), 
		max(a.cd_motivo_alta) 
	into STRICT	nr_atendimento_w, 
		cd_motivo_alta_w 
	from	atendimento_paciente a, 
		sus_laudo_paciente b 
	where	b.nr_atendimento = a.nr_atendimento 
	and	b.nr_seq_interno = nr_seq_interno_p;
	exception 
		when others then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(221835);
		/*Não foi possível obter os dados do atendimento durante a geração da APAC através do laudo.*/
 
		end;
 
	begin 
	select	b.cd_motivo_cobranca 
	into STRICT	cd_motivo_cobranca_w 
	from	motivo_alta a, 
		sus_motivo_cobr_unif b 
	where	a.cd_motivo_alta_sus = b.cd_motivo_cobranca 
	and	a.cd_motivo_alta = cd_motivo_alta_w;
	exception 
		when others then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(221836);
		/*Não foi possível obter o motivo de alta do paciente durante a geração da APAC através do laudo.*/
 
		end;
 
	if (coalesce(nr_atendimento_w,0) <> 0) and (coalesce(cd_motivo_cobranca_w,'0') <> '0') then 
		begin 
		CALL sus_gerar_apac_laudo_unif(nr_apac_p,nr_atendimento_w,cd_motivo_cobranca_w,null,null,nm_usuario_p);
		end;
	end if;
 
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
