SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_vincular_bpa_conta ( nr_atendimento_p bigint, nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w  sus_bpa_unif.nr_sequencia%type;


BEGIN

begin

/*Verifica se existe algum BPA sem vinculo de conta*/

select  min(nr_sequencia)
into STRICT    nr_sequencia_w
from    sus_bpa_unif
where   nr_atendimento = nr_atendimento_p
and     coalesce(nr_interno_conta::text, '') = '';
exception
when others then
        nr_sequencia_w	:= 0;
end;

/*Se existir faz o vinculo da conta no BPA*/

if (nr_sequencia_w <> 0) then
        update  sus_bpa_unif
        set     nr_interno_conta = nr_interno_conta_p,
                dt_atualizacao  = clock_timestamp(),
                nm_usuario      = nm_usuario_p
        where   nr_sequencia  = nr_sequencia_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
