CREATE OR REPLACE PROCEDURE sus_vincular_conta_prot_aih ( nr_interno_conta_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
ie_tipo_protocolo_w	smallint;
nr_seq_protocolo_w	bigint	:= 0;
cd_convenio_w		integer;


BEGIN 
 
select	cd_convenio_parametro 
into STRICT	cd_convenio_w 
from	conta_paciente 
where	nr_interno_conta	= nr_interno_conta_p;
 
select	coalesce(a.ie_tipo_protocolo_aih,1) 
into STRICT	ie_tipo_protocolo_w 
from	parametro_faturamento a 
where	a.cd_estabelecimento	= cd_estabelecimento_p;
 
select	coalesce(max(nr_seq_protocolo),0) 
into STRICT	nr_seq_protocolo_w 
from	protocolo_convenio 
where	ie_tipo_protocolo	= ie_tipo_protocolo_w 
and	cd_convenio		= cd_convenio_w 
and	ie_status_protocolo	= 1;
 
if (nr_seq_protocolo_w	> 0) then 
	update	conta_paciente 
	set	nr_seq_protocolo	= nr_seq_protocolo_w, 
		nm_usuario		= nm_usuario_p 
	where	nr_interno_conta	= nr_interno_conta_p 
	and	ie_status_acerto	= 2;
end if;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
