CREATE OR REPLACE PROCEDURE sus_vincular_laudo_conta ( nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_interno_w		bigint;
dt_periodo_inicial_w		timestamp;
dt_periodo_final_w		timestamp;
nr_atendimento_w		bigint;

C01 CURSOR FOR
	SELECT	nr_seq_interno
	from	sus_laudo_paciente
	where	nr_atendimento = nr_atendimento_w
	and	dt_emissao between dt_periodo_inicial_w and dt_periodo_final_w
	and	ie_classificacao = 1
	and	ie_tipo_laudo_sus in (0,1,2)
	and	coalesce(nr_interno_conta::text, '') = ''
	order by nr_seq_interno;


BEGIN

select	dt_periodo_inicial,
	dt_periodo_final,
	nr_atendimento
into STRICT	dt_periodo_inicial_w,
	dt_periodo_final_w,
	nr_atendimento_w
from	conta_paciente
where	nr_interno_conta = nr_interno_conta_p;

open C01;
loop
fetch C01 into
	nr_seq_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	sus_laudo_paciente
	set	nr_interno_conta = nr_interno_conta_p,
		nm_usuario 	= nm_usuario_p,
		dt_atualizacao 	= clock_timestamp()
	where	nr_seq_interno 	= nr_seq_interno_w;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
