CREATE OR REPLACE PROCEDURE sus_vincular_proc_princ_aih ( nr_interno_conta_p bigint, nr_seq_proc_princ_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	procedimento_paciente
	where	nr_interno_conta = nr_interno_conta_p
	and	coalesce(nr_seq_proc_princ::text, '') = ''
	and	Sus_Obter_TipoReg_Proc(cd_procedimento,ie_origem_proced,'C',0) <> 3
	order by nr_sequencia;

BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	procedimento_paciente
	set	nr_seq_proc_princ 	= nr_seq_proc_princ_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia = nr_sequencia_w;

	end;
end loop;
close C01;

commit;

end;
