SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE swcf_approve_dl_template (nr_sequencia_p bigint, nm_usuario_p text, ie_action_p bigint, ds_hash_p text default null) AS $body$
BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
    if (ie_action_p = 1) then    	
        update  dl_template
        set	dt_aprovacao = clock_timestamp(),
            nm_usuario_aprovacao = nm_usuario_p,
            ie_status = 'A',
            ds_hash = ds_hash_p,
            dt_atualizacao = clock_timestamp(),
            nm_usuario = nm_usuario_p
        where   nr_sequencia = nr_sequencia_p;
    elsif (ie_action_p = 2) then
        update  dl_template
        set	dt_aprovacao  = NULL,
            nm_usuario_aprovacao  = NULL,
            ie_status = 'L',
            ds_hash  = NULL,
            dt_atualizacao = clock_timestamp(),
            nm_usuario = nm_usuario_p
        where   nr_sequencia = nr_sequencia_p;
    end if;

    commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
