SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE swisslog_inserir_prescr ( nr_prescricao_p bigint, nr_seq_horario_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_setor_atendimento_w		setor_atendimento.cd_setor_atendimento%type;
nr_seq_empresa_w			empresa_integracao.nr_sequencia%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
ds_param_integ_hl7_w		varchar(4000);
ie_existe_w					varchar(1);
dt_prescricao_w				timestamp;
nr_atendimento_w			bigint;


BEGIN
begin
select	max(cd_setor_atendimento),
		max(cd_estabelecimento)
into STRICT	cd_setor_atendimento_w,
		cd_estabelecimento_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

select	max(nr_sequencia)
into STRICT	nr_seq_empresa_w
from	empresa_integracao
where	upper(nm_empresa) = 'SWISSLOG';

select	'S'
into STRICT	ie_existe_w
from	far_setores_integracao
where	nr_seq_empresa_int = nr_seq_empresa_w
and	cd_setor_atendimento = cd_setor_atendimento_w;
exception
when others then
	nr_seq_empresa_w := 0;
	ie_existe_w := 'N';
end;

if (ie_existe_w = 'S') then
	
	select	coalesce(max(dt_prescricao),clock_timestamp()),
			max(nr_atendimento)
	into STRICT 	dt_prescricao_w,
			nr_atendimento_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;
	
	ds_param_integ_hl7_w := 'nr_atendimento=' || nr_atendimento_w || obter_separador_bv ||
				'nr_prescricao=' || nr_prescricao_p || obter_separador_bv ||
				'ie_aprazado=' || to_char(nr_seq_horario_p) || obter_separador_bv ||
				'ie_origem=' || 'GLAP' || obter_separador_bv ||
				'nr_seq_horario_p=' || nr_seq_horario_p || obter_separador_bv;

	if (obter_se_envia_swisslog(434, nr_prescricao_p, 'GLAP', to_char(nr_seq_horario_p)) = 'S') then
		CALL swisslog_gerar_integracao(434, ds_param_integ_hl7_w);
		
		/*update	ap_lote 
		set 	ie_integracao = 'W'
		where 	nr_prescricao = nr_prescricao_p;*/
	end if;
	
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
