CREATE OR REPLACE PROCEDURE tasylab_cad_exame_lab_antib ( nr_origem_p bigint, nr_seq_resultado_p bigint, nr_seq_result_item_p bigint, cd_medicamento_p text, ie_resultado_p text, dt_atualizacao_p timestamp, nm_usuario_p text, qt_microorganismo_p text, ds_obs_microorganismo_p text, qt_mic_p bigint, ie_micro_sem_antib_p text, ds_resultado_antib_p text, ie_primeira_escolha_p text, ds_microorganismo_integr_p text, nr_seq_prescr_p bigint, cd_erro_p INOUT bigint, ds_erro_p INOUT text ) AS $body$
DECLARE


cd_medico_w		varchar(10);
nr_seq_exame_w		varchar(10);
nr_seq_formato_w	bigint;
cd_microorganismo_w	bigint;
cd_medicamento_w	bigint;
nr_seq_result_item_w	bigint;


BEGIN

cd_erro_p	:= 0;

if (coalesce(ds_microorganismo_integr_p::text, '') = '') then
	cd_erro_p	:= 28;
else

	select	max(a.cd_microorganismo)
	into STRICT	cd_microorganismo_w
	from	cih_microorganismo a
	where	a.ds_microorganismo_integr = ds_microorganismo_integr_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_result_item_w
	from	exame_lab_result_item
	where	nr_seq_resultado = nr_seq_resultado_p
	and	nr_seq_prescr = nr_seq_prescr_p;

	if (coalesce(cd_microorganismo_w::text, '') = '') then
		cd_erro_p	:= 29;
	elsif (coalesce(nr_seq_result_item_w::text, '') = '') then
		cd_erro_p	:= 30;
	else
		select	max(a.cd_medicamento)
		into STRICT	cd_medicamento_w
		from	cih_medicamento a
		where	a.ds_codigo_integr = cd_medicamento_p;

		begin
		insert into exame_lab_result_antib(	nr_sequencia,
							nr_seq_resultado,
							nr_seq_result_item,
							cd_microorganismo,
							cd_medicamento,
							ie_resultado,
							dt_atualizacao,
							nm_usuario,
							qt_microorganismo,
							ds_obs_microorganismo,
							qt_mic,
							ie_micro_sem_antib,
							ds_resultado_antib,
							ie_primeira_escolha)
					values (	nextval('exame_lab_result_antib_seq'),
							nr_seq_resultado_p,
							nr_seq_result_item_w, --nr_seq_result_item_p,
							cd_microorganismo_w,
							cd_medicamento_w,
							ie_resultado_p,
							dt_atualizacao_p,
							nm_usuario_p,
							qt_microorganismo_p,
							ds_obs_microorganismo_p,
							qt_mic_p,
							ie_micro_sem_antib_p,
							ds_resultado_antib_p,
							ie_primeira_escolha_p);

		--OS727249 - Ivan
		--commit;
		exception
		when others then
			cd_erro_p	:= 1;
			ds_erro_p	:= substr('Erro ao registrar o antibiograma '||sqlerrm,1,2000);
		end;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
