CREATE OR REPLACE PROCEDURE tasylab_cad_exame_stat_exame ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_status_atend_p text, ie_status_execucao_p text, cd_motivo_baixa_p bigint, nr_seq_externo_p bigint, cd_erro_p INOUT bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_status_atend_w	 prescr_procedimento.ie_status_atend%type;
dt_coleta_w			 prescr_procedimento.dt_coleta%type;

nr_sequencia_w		 prescr_proc_etapa.nr_sequencia%type;
ie_status_recoleta_w prescr_proc_etapa.ie_etapa%type;


BEGIN

begin

cd_erro_p	:= 0;

CALL tasy_atualizar_dados_sessao(nr_seq_externo_p);

if (ie_status_atend_p = 20) then

	select	max(a.ie_status_atend),
			max(a.dt_coleta)
	into STRICT	ie_status_atend_w,
			dt_coleta_w
	from	prescr_procedimento a
	where	nr_prescricao		= nr_prescricao_p
	and		nr_sequencia		= nr_seq_prescricao_p;


	select 	max(a.IE_STATUS_RECOLETA)
	into STRICT 	ie_status_recoleta_w
	from 	LAB_PARAMETRO a,
			prescr_medica b
	where 	a.cd_estabelecimento = b.cd_estabelecimento
	and 	b.nr_prescricao = nr_prescricao_p;


	if (ie_status_atend_w < 20 /*and ie_status_atend_w <> ie_status_recoleta_w*/
) then
		update	prescr_procedimento
		set		ie_status_atend 	= ie_status_atend_p,
				ie_status_execucao	= ie_status_execucao_p,
				cd_motivo_baixa		= cd_motivo_baixa_p,
				dt_atualizacao		= clock_timestamp(),
				nm_usuario			= 'TasyLab'
		where	nr_prescricao		= nr_prescricao_p
		and		nr_sequencia		= nr_seq_prescricao_p
		and		coalesce(ie_status_atend,0) < ie_status_atend_p;

		--OS727249 - Ivan
		--commit;
	else

		select	max(a.nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_proc_etapa a
		where	nr_prescricao = nr_prescricao_p
		and		nr_seq_prescricao = nr_seq_prescricao_p
		and		ie_etapa = 20;

		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
			update	prescr_proc_etapa
			set		dt_atualizacao = coalesce(dt_coleta_w,dt_atualizacao)
			where	nr_sequencia = nr_sequencia_w;
			--OS727249 - Ivan
			--commit;
		end if;

	end if;
end if;

exception
when others then
	cd_erro_p	:= 1;
	ds_erro_p	:= substr('Erro ao alterar o status do procedimento '||sqlerrm,1,2000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE tasylab_cad_exame_stat_exame ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_status_atend_p text, ie_status_execucao_p text, cd_motivo_baixa_p bigint, nr_seq_externo_p bigint, cd_erro_p INOUT bigint, ds_erro_p INOUT text) FROM PUBLIC;

