SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tasylab_limpar_prescr_proc_mat ( nr_prescricao_p bigint, nr_seq_externo_p bigint, cd_erro_p INOUT bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_existe_w		varchar(1);

C01 CURSOR FOR
	SELECT	distinct
			b.nr_sequencia,
			a.nr_sequencia nr_seq_prescr_mat
	FROM prescr_procedimento c, prescr_proc_material a
LEFT OUTER JOIN prescr_proc_mat_item b ON (a.nr_sequencia = b.nr_seq_prescr_proc_mat AND a.nr_prescricao = b.nr_prescricao)
WHERE a.nr_prescricao = nr_prescricao_p and c.nr_prescricao = a.nr_prescricao and c.nr_sequencia = b.nr_seq_prescr and c.ie_status_atend <= 30;/*tratamento para não excluir item caso já esteja no status digitação ou posterior*/
c01_w	c01%rowtype;


BEGIN

cd_erro_p	:= 0;

CALL tasy_atualizar_dados_sessao(nr_seq_externo_p);

begin
open c01;
loop
fetch c01 into c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	delete 	FROM prescr_proc_mat_item
	where	nr_prescricao = nr_prescricao_p
	and		nr_sequencia = c01_w.nr_sequencia;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_existe_w
	from	prescr_proc_material a
	where	a.nr_sequencia = c01_w.nr_seq_prescr_mat;

	if (ie_existe_w = 'N') then
		delete 	FROM prescr_proc_material
		where	nr_sequencia = c01_w.nr_seq_prescr_mat/*
		and		nm_usuario = 'TasyLab-LibPre'*/
;
	end if;

	end;
end loop;
close c01;

exception
when others then
	cd_erro_p	:= 1;
	ds_erro_p	:= substr('Erro ao limpas as barras do procedimento '||sqlerrm,1,2000);
end;
--OS727249 - Ivan
--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
