				(SELECT max(nr_sequencia)
				from	tipo_indice_reajuste b
				where	b.cd_estabelecimento = v03.cd_estabelecimento),
				v03.cd_estabelecimento
			from	pat_param_contabil a
			where	coalesce(cd_estabelecimento::text, '') = '';
		end;
	end loop;
	close c03;

	commit;

	delete	from pat_param_contabil
	where	coalesce(cd_estabelecimento::text, '') = '';

	commit;

	select	count(*)
	into STRICT	qt_registro_w
	from	user_tab_columns
	where	table_name = 'PAT_PARAM_CONTABIL'
	and	column_name = 'CD_ESTABELECIMENTO'
	and	nullable = 'Y';

	if (coalesce(qt_registro_w,0) > 0)then
		CALL Exec_sql_Dinamico('Tasy','alter table pat_param_contabil modify cd_estabelecimento not null');
	end if;

	end;
end if;

if (dt_versao_atual_cliente_w < to_date('10/05/2016', 'dd/mm/yyyy')) then

	select	count(*)
	into STRICT	qt_registro_w
	from	user_constraints
	where	table_name	= 'DIOPS_TRANS_CONTA'
	and	constraint_name	= 'DIOPSTC_UK';

	if (qt_registro_w > 0) then
		CALL exec_sql_dinamico('mropelato','alter table DIOPS_TRANS_CONTA drop constraint DIOPSTC_UK');
	end if;

	select	count(*)
	into STRICT	qt_registro_w
	from	user_indexes
	where	index_name	= 'DIOPSTC_UK';

	if (qt_registro_w > 0) then
		CALL exec_sql_dinamico('mropelato','drop index DIOPSTC_UK');
	end if;

	select	count(*)
	into STRICT	qt_registro_w
	from	indice_atributo
	where	nm_tabela = 'DIOPS_TRANS_CONTA'
	and	nm_indice = 'DIOPSTC_UK';

	if (qt_registro_w > 0) then
		CALL exec_sql_dinamico('mropelato','delete from indice_atributo where nm_tabela = ''DIOPS_TRANS_CONTA'' and nm_indice = ''DIOPSTC_UK''');
	end if;

	commit;

	select	count(*)
	into STRICT	qt_registro_w
	from	indice
	where	nm_tabela = 'DIOPS_TRANS_CONTA'
	and	nm_indice = 'DIOPSTC_UK';

	if (qt_registro_w > 0) then
		CALL exec_sql_dinamico('mropelato','delete from indice where nm_tabela = ''DIOPS_TRANS_CONTA'' and nm_indice = ''DIOPSTC_UK''');
	end if;

	commit;

end if;

if (dt_versao_atual_cliente_w < to_date('01/06/2016', 'dd/mm/yyyy')) then
	begin
	select	count(1)
	into STRICT	qt_registro_w
	from	tipo_lote_contabil
	where	cd_tipo_lote_contabil	= 24
	and	upper(nm_objeto) = 'CTB_PLS_CONTABILIZA_COM_VENDA';

	if (qt_registro_w = 0) then
		CALL exec_sql_dinamico('Tasy','update TIPO_LOTE_CONTABIL set nm_objeto = ''CTB_PLS_CONTABILIZA_COM_VENDA'' where CD_TIPO_LOTE_CONTABIL = 24');
		commit;
	end if;

	end;
end if;

if (dt_versao_atual_cliente_w < to_date('24/06/2016', 'dd/mm/yyyy')) then
	begin

	for valor_w in (
		SELECT	distinct a.dt_valor
		from	pat_valor_bem a
		order by 1 desc
	) loop
		begin
		update	pat_valor_bem a
		set	a.cd_estabelecimento = pat_obter_estab_bem_periodo(a.nr_seq_bem,a.dt_valor)
		where	coalesce(cd_estabelecimento,0) = 0
		and	a.dt_valor = valor_w.dt_valor;
		commit;
		end;
	end loop;
	end;
end if;


end;
$body$
