SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tasy_ajustar_funcao () AS $body$
DECLARE


qt_registro_w			bigint;


BEGIN

/* Ajustar base de Funções, Parametros usuários  */

select	count(*)
into STRICT	qt_registro_w
from	funcao;

if (qt_registro_w > 0) then
	delete from funcao_parametro a
	where not exists (
		SELECT 1
		from funcao b
		where a.cd_funcao = b.cd_funcao);

	delete from relatorio_funcao a
	where not exists (
		SELECT 1
		from funcao b
		where a.cd_funcao = b.cd_funcao);

	delete from tabela_atrib_regra a
	where	(cd_funcao IS NOT NULL AND cd_funcao::text <> '')
	and	not exists (
		SELECT 1
		from funcao b
		where a.cd_funcao = b.cd_funcao);
	update	usuario a
	set	cd_funcao  = NULL
	where	(cd_funcao IS NOT NULL AND cd_funcao::text <> '')
	and	not exists (
		SELECT 1
		from funcao b
		where a.cd_funcao = b.cd_funcao);

	delete from funcao_net a
	where not exists (
		SELECT 1
		from funcao b
		where a.cd_funcao = b.cd_funcao);

	commit;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE tasy_ajustar_funcao () FROM PUBLIC;

