SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tasy_ajustar_integridade () AS $body$
DECLARE


nm_tabela_w		varchar(50)      := null;
nm_Integridade_w	varchar(50)      := null;

C010 CURSOR FOR
SELECT nm_tabela,
	nm_integridade_referencial
from	integridade_referencial a
where	exists (
	SELECT 1
	from	user_constraints b
	where	a.nm_integridade_referencial = b.constraint_name
	and	a.nm_tabela			= b.table_name
	and	a.ie_regra_delecao 		<> b.DELETE_RULE);


BEGIN

OPEN C010;
LOOP
	FETCH C010 INTO 	nm_tabela_w,
				nm_integridade_w;
	EXIT WHEN NOT FOUND; /* apply on C010 */
		CALL Tasy_Criar_Integridade(nm_tabela_w, nm_integridade_w, 'S');
END LOOP;
CLOSE C010;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
