SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tasy_ajustar_sequence (nr_seq_log_erro_p bigint) AS $body$
DECLARE


nm_sequence_w	varchar(40);
qt_cache_w	bigint;
ds_comando_w	varchar(255);

C01 CURSOR FOR
	SELECT 	a.sequence_name,
		b.qt_cache
	FROM 	user_sequences a,
		tabela_atributo b
	WHERE	(b.qt_cache IS NOT NULL AND b.qt_cache::text <> '')
	AND	b.qt_cache <> a.cache_size
	AND	a.sequence_name = b.nm_tabela || '_SEQ';


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nm_sequence_w,
	qt_cache_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		ds_comando_w := 'ALTER SEQUENCE ' || nm_sequence_w || ' CACHE ' || qt_cache_w;
		CALL exec_sql_dinamico('Acerto Cache Sequence',ds_comando_w);
	exception
	when others then
		begin
			CALL Gravar_Log_Atualizacao_Erro(nr_seq_log_erro_p,'TASY_CRIAR_TRIGGER_LOG_UPDATE',substr(SQLERRM(SQLSTATE),1,1800), 'Tasy');
		end;
	end;
END LOOP;
CLOSE C01;


END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
